    public static void loadData()
    {
        AhkPets pet;

        pet = AhkPets::find("Lucky");
        if(!pet)
        {
            pet.Name = "Lucky";
            pet.Age = 10;
            pet.Type = AhkPetType::Dog;
            pet.insert();
        }
        
        pet = AhkPets::find("Baloo");
        if(!pet)
        {
            pet.Name = "Baloo";
            pet.Age = 2;
            pet.Type = AhkPetType::Cat;
            pet.insert();
        }
        
        pet = AhkPets::find("Simba");
        if(!pet)
        {
            pet.Name = "Simba";
            pet.Age = 1;
            pet.Type = AhkPetType::Cat;
            pet.insert();
        }
    }
